from PySide2.QtWidgets import QLabel, QWidget, QPushButton, QApplication, QVBoxLayout, QSlider, QSpinBoxclass Window(QWidget):    def __init__(self):        QWidget.__init__(self)        self.v_layout=QVBoxLayout()        self.label = QLabel("Ceci est un QLabel")        self.button = QPushButton("Ceci est un QPushButton")        self.slider = QSlider()        self.spinbox=QSpinBox()        self.v_layout.addWidget(self.label)        self.v_layout.addWidget(self.button)        self.v_layout.addWidget(self.slider)        self.v_layout.addWidget(self.spinbox)        self.setLayout(self.v_layout)if __name__=="__main__":    app=QApplication([])    win=Window()    win.show()    app.exec_()